#include<iostream>
using namespace std;

int main(){

    int day;
    cin>>day;

    switch(day){

    case 1:
    cout<<"Monday"<<endl;
    break;

    case 2:
    cout<<"tuesday"<<endl;
    break;

    case 3:
    cout<<"wednesday"<<endl;
    break;

    case 4:
    cout<<"thusday"<<endl;
    break;

    case 5:
    cout<<"friday"<<endl;
    break;
    
    default:
    cout<<"default case"<<endl;
    
    }

    

    return 0;
}